        stop: function() {
          animating = false;
        },
        easing: function (x, t, b, c, data) {
          return c * Math.sqrt(1 - (t=t/data-1)*t) + b;
        }
      }, point);

      this.data(data);

      frame();

    } else {
      
      delete data['effect'];

    }
  }
});

// Export some globals

$.isTouch = isTouch;
$.mouseEvents = mouseEvents;
$.cssPrefix = getPrefix;
$.cssTransitionEnd = getTransitionEnd;
$.findPos = findPos;

})(jQuery);

styles:
/* jQuery iCalendar styles v1.1.1 */
.icalendar_popup_text {
	cursor: pointer;
	color: blue;
	text-decoration: underline;
}
.icalendar_popup {
	display: none;
	position: absolute;
	z-index: 100;
}
.icalendar_popup ul {
	float: left;
	width: 220px;
	padding: 2px;
	background-color: #eee;
	border: 1px solid #aaa;
}
.icalendar_popup .icalendar_list li {
	width: 50%;
	margin: 0%;
	padding: 2px 0px;
	background-color: transparent;
	border: none;
}
.icalendar_list {
	display: block;
	list-style-type: none;
	font-size: 80%;
	margin: 0px;
	padding: 0px;
}
@media screen and (-webkit-min-device-pixel-ratio:0) {  /* Safari3/Opera9 */
	.icalendar_list {
		font-size: 100%;
	}
}
html:first-child .icalendar_list {  /* Opera */
	font-size: 80%;
}
.icalendar_list li {
	float: left;
	width: 12%;
	margin: 0px 1px;
	padding: 1px;
	background-color: #eee;
	border: 1px outset;
}
.icalendar_compact {
	background-color: #eee;
	border: 1px outset;
}
.icalendar_compact li {
	width: 16px;
	height: 16px;
	margin: 0px;
	padding: 2px;
	background-color: transparent;
	border: none;
}
.icalendar_list a {
	width: 100%;
	text-decoration: none;
}
.icalendar_list span {
	display: inline-block;
	width: 16px;
	height: 16px;
}
.icalendar_list img {
	border: none;
}
